import { useState, useMemo } from "react";
import { format, addDays, getWeek } from "date-fns";
import jsPDF from "jspdf";
import { tr, nl, enUS, arSA } from "date-fns/locale";
import { CalendarDays, ChevronDown, Pencil, Trash2, Loader2, Share2, MapPin, UserCheck } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useAuth } from "@/hooks/useAuth";
import { useLanguage, Language } from "@/hooks/useLanguage";
import { EditActivityDialog } from "./EditActivityDialog";
import { toast } from "sonner";
import { cn } from "@/lib/utils";

const dateLocales: Record<Language, typeof nl> = { tr, en: enUS, nl, ar: arSA };
const DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'] as const;

export function ActivityCard({ activity, onDelete, onUpdate, onDeletePhoto, onUploadPhotos, onRefetch, canEdit }: any) {
  const { isAdmin } = useAuth();
  const { t, language } = useLanguage();
  const [isExpanded, setIsExpanded] = useState(false);
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);

  const labels = useMemo(() => ({
    report: t("activity.workReport"),
    opsteller: t("activity.preparedBy"),
    uitvoerder: t("activity.supervisor"),
    period: t("activity.period"),
    day: t("activity.day"),
    desc: t("activity.description"),
    meerwerk: t("activity.extraWork"),
    locatie: t("activity.location"),
    hours: t("activity.hours"),
    send: t("activity.send"),
    edit: t("activity.edit"),
    delete: t("activity.delete"),
    total: t("activity.total"),
    monday: t("days.monday"),
    tuesday: t("days.tuesday"),
    wednesday: t("days.wednesday"),
    thursday: t("days.thursday"),
    friday: t("days.friday"),
    saturday: t("days.saturday")
  }), [t]);
  const data = (() => { try { return JSON.parse(activity?.description || "{}"); } catch (e) { return {}; } })();
  const weekNumber = activity?.activity_date ? getWeek(new Date(activity.activity_date), { weekStartsOn: 1 }) : 0;

  // Fotoğrafları PDF için optimize etme
  const getOptimizedImage = async (url: string): Promise<string> => {
    return new Promise((resolve, reject) => {
      const img = new Image();
      img.crossOrigin = "anonymous";
      img.src = url;
      img.onload = () => {
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");
        const maxW = 500;
        const scale = maxW / img.width;
        canvas.width = maxW;
        canvas.height = img.height * scale;
        ctx?.drawImage(img, 0, 0, canvas.width, canvas.height);
        resolve(canvas.toDataURL("image/jpeg", 0.6));
      };
      img.onerror = reject;
    });
  };

  const handleShare = async (e: React.MouseEvent) => {
    e.preventDefault(); e.stopPropagation();
    setIsGenerating(true);

    try {
      const doc = new jsPDF('p', 'mm', 'a4');
      const margin = 15;
      let y = 50;
      const pageWidth = 210;
      const col = { day: margin + 2, desc: margin + 22, meer: margin + 95, loc: margin + 135, hours: margin + 175 };

      // DUTCH PDF LABELS (Hardcoded)
      const pdfLabels = {
        report: "WERKRAPPORT",
        opsteller: "Opsteller",
        uitvoerder: "Uitvoerder",
        period: "Periode",
        day: "Dag",
        desc: "Beschrijving",
        meerwerk: "Meerwerk",
        locatie: "Locatie",
        hours: "Uren",
        total: "TOTAAL",
        week: "Week",
        generatedOn: "GEGENEREERD OP"
      };

      // PROFESSIONAL HEADER
      doc.setFillColor(30, 41, 59); // Slate 800
      doc.rect(0, 0, pageWidth, 40, 'F');

      // Logo/Title
      doc.setTextColor(255, 255, 255);
      doc.setFont("helvetica", "bold");
      doc.setFontSize(24);
      doc.text("EA APP", margin, 20);

      doc.setFontSize(10);
      doc.setFont("helvetica", "normal");
      doc.text("PROFESSIONAL WORK REPORTING SYSTEM", margin, 28);

      doc.setFontSize(18);
      doc.setFont("helvetica", "bold");
      doc.text(pdfLabels.report.toUpperCase(), pageWidth - margin, 25, { align: 'right' });

      // PROJECT INFO
      doc.setTextColor(50, 50, 50);
      doc.setFontSize(9);
      doc.setFont("helvetica", "bold");
      doc.text(pdfLabels.period.toUpperCase(), margin, y - 4);
      doc.setFont("helvetica", "normal");
      doc.text(`${format(new Date(activity.activity_date), "dd.MM.yyyy", { locale: nl })} - ${format(addDays(new Date(activity.activity_date), 5), "dd.MM.yyyy", { locale: nl })}`, margin, y);

      doc.setFont("helvetica", "bold");
      doc.text(pdfLabels.opsteller.toUpperCase(), margin + 70, y - 4);
      doc.setFont("helvetica", "normal");
      doc.text(activity?.profile?.full_name || "-", margin + 70, y);

      const firstDay = Object.values(data?.days || {})[0] as any;
      doc.setFont("helvetica", "bold");
      doc.text(pdfLabels.uitvoerder.toUpperCase(), margin + 130, y - 4);
      doc.setFont("helvetica", "normal");
      doc.text(firstDay?.uitvoerder || "-", margin + 130, y);

      y += 15;

      // TABLE HEADER
      doc.setFillColor(248, 250, 252); // Slate 50
      doc.rect(margin, y, 180, 10, 'F');
      doc.setDrawColor(226, 232, 240); // Slate 200
      doc.line(margin, y, margin + 180, y);
      doc.line(margin, y + 10, margin + 180, y + 10);

      doc.setFontSize(8);
      doc.setFont("helvetica", "bold");
      doc.setTextColor(100, 116, 139);
      doc.text(pdfLabels.day.toUpperCase(), col.day, y + 6.5);
      doc.text(pdfLabels.desc.toUpperCase(), col.desc, y + 6.5);
      doc.text(pdfLabels.meerwerk.toUpperCase(), col.meer, y + 6.5);
      doc.text(pdfLabels.locatie.toUpperCase(), col.loc, y + 6.5);
      doc.text(pdfLabels.hours.toUpperCase(), col.hours, y + 6.5, { align: 'right' });

      y += 10;
      doc.setFont("helvetica", "normal");
      doc.setTextColor(30, 41, 59);

      // nl date-fns day names
      const nlDays: Record<string, string> = {
        monday: "Maandag",
        tuesday: "Dinsdag",
        wednesday: "Woensdag",
        thursday: "Donderdag",
        friday: "Vrijdag",
        saturday: "Zaterdag"
      };

      for (const dayKey of DAYS) {
        const entry = data?.days?.[dayKey];
        if (!entry || (!entry.work && !entry.hours)) continue;

        const dLines = doc.splitTextToSize(entry.work || "-", 70);
        const mLines = doc.splitTextToSize(entry.meerwerk || "-", 35);
        const lLines = doc.splitTextToSize(entry.location || "-", 35);

        const rowH = Math.max(15, dLines.length * 5 + 6, mLines.length * 5 + 6);
        if (y + rowH > 270) {
          doc.addPage();
          y = 20;
          doc.setFillColor(248, 250, 252);
          doc.rect(margin, y, 180, 10, 'F');
          y += 10;
        }

        doc.setDrawColor(241, 245, 249);
        doc.line(margin, y + rowH, margin + 180, y + rowH);

        doc.setFont("helvetica", "bold");
        doc.text(nlDays[dayKey].toUpperCase(), col.day, y + 8);

        doc.setFont("helvetica", "normal");
        doc.setFontSize(9);
        doc.text(dLines, col.desc, y + 8);

        doc.setFontSize(8);
        doc.setTextColor(100, 116, 139);
        doc.text(mLines, col.meer, y + 8);
        doc.text(lLines, col.loc, y + 8);

        doc.setTextColor(30, 41, 59);
        doc.setFont("helvetica", "bold");
        doc.text(`${entry.hours}H`, col.hours, y + 8, { align: 'right' });

        y += rowH;

        // HIGH QUALITY PHOTO GRID
        const dayPhotos = activity?.photos?.filter((p: any) => p.day === dayKey);
        if (dayPhotos?.length > 0) {
          y += 5;
          let xImg = col.desc;
          const imgSize = 35;
          const spacing = 5;

          for (const photo of dayPhotos) {
            if (y + imgSize > 275) { doc.addPage(); y = 20; xImg = col.desc; }
            try {
              const imgData = await getOptimizedImage(photo.file_url);
              doc.setDrawColor(226, 232, 240);
              doc.rect(xImg - 0.5, y - 0.5, imgSize + 1, imgSize + 1); // Photo Border
              doc.addImage(imgData, 'JPEG', xImg, y, imgSize, imgSize);
              xImg += imgSize + spacing;
              if (xImg + imgSize > col.hours + 5) { xImg = col.desc; y += imgSize + spacing; }
            } catch (e) { console.error("PDF: Photo error", e); }
          }
          y += (xImg === col.desc) ? 10 : imgSize + 10;
        }
      }

      // TOTAL SUMMARY
      y += 10;
      if (y > 260) { doc.addPage(); y = 20; }

      doc.setFillColor(241, 245, 249);
      doc.rect(margin + 120, y, 60, 15, 'F');
      doc.setDrawColor(30, 41, 59);
      doc.line(margin + 120, y, margin + 180, y);

      doc.setFontSize(10);
      doc.setFont("helvetica", "bold");
      doc.setTextColor(30, 41, 59);
      doc.text(pdfLabels.total.toUpperCase(), margin + 125, y + 9.5);
      doc.setFontSize(14);
      doc.text(`${data?.totalHours || 0} UREN`, margin + 175, y + 10, { align: 'right' });

      // FOOTER
      const pageCount = doc.getNumberOfPages();
      for (let i = 1; i <= pageCount; i++) {
        doc.setPage(i);
        doc.setFontSize(8);
        doc.setTextColor(150, 150, 150);
        doc.text(`EA APP - OFFICIEEL WERKRAPPORT - PAGINA ${i} VAN ${pageCount}`, pageWidth / 2, 285, { align: 'center' });
        doc.text(`${pdfLabels.generatedOn}: ${format(new Date(), "dd.MM.yyyy HH:mm", { locale: nl })}`, margin, 285);
      }

      // FINALIZING
      const fileName = `Werkrapport_Week${weekNumber}_${activity?.profile?.full_name?.replace(/ /g, '_') || 'Rapport'}.pdf`;
      const pdfBlob = doc.output('blob');
      const file = new File([pdfBlob], fileName, { type: "application/pdf" });

      if (navigator.canShare && navigator.canShare({ files: [file] })) {
        await navigator.share({ files: [file], title: pdfLabels.report });
      } else {
        doc.save(fileName);
      }
    } catch (err) {
      console.error("PDF Generation Error:", err);
      toast.error(t("activity.error"));
    } finally {
      setIsGenerating(false);
    }
  };

  return (
    <div className="bg-card rounded-[2.5rem] border border-border/40 mb-8 overflow-hidden shadow-sm hover:shadow-xl hover:border-primary/20 transition-all duration-500 group">
      <div
        onClick={() => setIsExpanded(!isExpanded)}
        className="p-8 cursor-pointer flex justify-between items-center bg-gradient-to-br from-white to-muted/30 relative overflow-hidden"
      >
        <div className="absolute top-0 right-0 w-32 h-32 bg-primary/5 rounded-full -mr-16 -mt-16 transition-transform group-hover:scale-110" />

        <div className="flex items-center gap-6 relative z-10">
          <div className="bg-primary p-4 rounded-2xl text-white shadow-lg shadow-primary/20 transition-transform group-hover:rotate-3">
            <CalendarDays className="w-7 h-7" />
          </div>
          <div>
            <div className="flex items-center gap-2 mb-1">
              <span className="text-[10px] font-black text-primary uppercase tracking-[0.2em]">{t('activity.week')} {weekNumber}</span>
              <div className="w-1 h-1 rounded-full bg-border" />
              <span className="text-[10px] font-bold text-muted-foreground uppercase tracking-widest">{activity?.profile?.full_name || "-"}</span>
            </div>
            <p className="font-black text-foreground text-2xl tracking-tight">
              {activity?.activity_date ? format(new Date(activity.activity_date), "MMMM yyyy", { locale: dateLocales[language] || nl }) : '-'}
            </p>
          </div>
        </div>

        <div className="flex items-center gap-6 relative z-10">
          <div className="flex flex-col items-end">
            <span className="text-[10px] font-black text-muted-foreground uppercase tracking-widest">{labels.hours}</span>
            <span className="font-black text-foreground text-3xl tabular-nums">{data?.totalHours || 0}</span>
          </div>
          <div className="h-10 w-10 flex items-center justify-center rounded-xl bg-muted/50 text-muted-foreground group-hover:bg-primary group-hover:text-white transition-all duration-300">
            <ChevronDown className={cn("w-6 h-6 transition-transform duration-500", isExpanded && "rotate-180")} />
          </div>
        </div>
      </div>

      {isExpanded && (
        <div className="p-8 pt-0 space-y-8 animate-in slide-in-from-top-4 duration-500">
          {/* Action Bar */}
          <div className="flex flex-wrap items-center justify-between gap-4 bg-muted/30 p-4 rounded-[2rem] border border-border/50">
            <div className="flex items-center gap-3">
              {canEdit && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={(e) => { e.stopPropagation(); setEditDialogOpen(true); }}
                  className="rounded-xl font-black bg-white h-11 px-5 text-[11px] uppercase tracking-widest hover:border-primary hover:text-primary transition-all active:scale-95"
                >
                  <Pencil className="w-3.5 h-3.5 mr-2" /> {labels.edit}
                </Button>
              )}
              {isAdmin && (
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={(e) => { e.stopPropagation(); onDelete(activity.id); }}
                  className="rounded-xl font-black text-destructive h-11 px-5 text-[11px] uppercase tracking-widest hover:bg-destructive/10 transition-all active:scale-95"
                >
                  <Trash2 className="w-3.5 h-3.5 mr-2" /> {labels.delete}
                </Button>
              )}
            </div>
            <Button
              disabled={isGenerating}
              onClick={handleShare}
              className="bg-foreground text-background rounded-xl h-12 px-8 font-black text-xs uppercase tracking-[0.2em] shadow-xl hover:bg-primary hover:text-white active:scale-95 transition-all w-full sm:w-auto flex items-center justify-center group/btn"
            >
              {isGenerating ? (
                <Loader2 className="w-4 h-4 animate-spin mr-2" />
              ) : (
                <Share2 className="w-4 h-4 mr-2 transition-transform group-hover/btn:scale-125" />
              )}
              {labels.send}
            </Button>
          </div>

          {/* Days Grid */}
          <div className="space-y-4">
            {DAYS.map((day) => {
              const entry = data?.days?.[day];
              if (!entry?.work && !entry?.hours) return null;

              const dayPhotos = activity?.photos?.filter((p: any) => p.day === day);

              return (
                <div key={day} className="bg-white rounded-3xl p-6 border border-border/50 shadow-sm hover:shadow-md transition-shadow relative group/day overflow-hidden">
                  <div className="absolute top-0 left-0 w-1 h-full bg-muted/20 group-hover/day:bg-primary/40 transition-colors" />

                  <div className="flex justify-between items-start mb-4">
                    <div className="space-y-1">
                      <span className="font-black text-primary text-xs uppercase tracking-[0.2em] italic">{labels[day]}</span>
                      {entry.location && (
                        <div className="flex items-center gap-1.5 text-[10px] font-bold text-muted-foreground uppercase opacity-60">
                          <MapPin className="w-3 h-3" /> {entry.location}
                        </div>
                      )}
                    </div>
                    <div className="bg-muted/50 px-4 py-1.5 rounded-full text-[11px] font-black border border-border/30 text-foreground shadow-inner">
                      {entry.hours}H
                    </div>
                  </div>

                  <p className="text-base text-foreground/80 font-medium mb-4 leading-relaxed pl-1 border-l-2 border-border/20">
                    {entry.work}
                  </p>

                  {entry.meerwerk && (
                    <div className="bg-primary/5 border border-primary/10 rounded-2xl p-4 mb-4 text-sm text-primary font-medium animate-in fade-in slide-in-from-left-2 duration-300">
                      <span className="font-black text-[10px] uppercase tracking-widest block mb-2 opacity-60 italic">{labels.meerwerk}</span>
                      {entry.meerwerk}
                    </div>
                  )}

                  {/* Day Photos Grid */}
                  {dayPhotos?.length > 0 && (
                    <div className="flex flex-wrap gap-3 mt-4">
                      {dayPhotos.map((photo: any, i: number) => (
                        <div key={i} className="relative w-20 h-20 rounded-2xl overflow-hidden border-2 border-white shadow-lg flex-shrink-0 group/photo hover:scale-110 transition-transform cursor-pointer">
                          <img
                            src={photo.file_url}
                            alt={`Preview ${i}`}
                            className="w-full h-full object-cover"
                          />
                        </div>
                      ))}
                    </div>
                  )}

                  {entry.uitvoerder && (
                    <div className="mt-4 pt-4 border-t border-border/30 flex items-center justify-between text-[10px] font-black uppercase tracking-widest text-muted-foreground/60">
                      <div className="flex items-center gap-2">
                        <UserCheck className="w-3 h-3" />
                        {labels.uitvoerder}: <span className="text-foreground">{entry.uitvoerder}</span>
                      </div>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      )}

      <EditActivityDialog
        activity={activity}
        open={editDialogOpen}
        onOpenChange={setEditDialogOpen}
        onSave={async (id, updateData) => {
          const res = await onUpdate(id, updateData);
          if (res) onRefetch();
          return res;
        }}
        onDeletePhoto={onDeletePhoto}
        onUploadPhotos={onUploadPhotos}
        onRefetch={onRefetch}
      />
    </div>
  );
}

import { useState, useRef } from "react";
import { format } from "date-fns";
import { nl, tr, enUS, arSA } from "date-fns/locale";
import { CalendarIcon, Camera, MapPin, Plus, X, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Calendar } from "@/components/ui/calendar";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import { cn } from "@/lib/utils";
import { useLanguage, Language } from "@/hooks/useLanguage";

const dateLocales: Record<Language, typeof nl> = {
  tr: tr,
  en: enUS,
  nl: nl,
  ar: arSA,
};

interface ActivityFormProps {
  onSubmit: (data: {
    description: string;
    location: string;
    date: Date;
    photos: File[];
  }) => Promise<any>;
}

export function ActivityForm({ onSubmit }: ActivityFormProps) {
  const [description, setDescription] = useState("");
  const [location, setLocation] = useState("");
  const [date, setDate] = useState<Date>(new Date());
  const [photos, setPhotos] = useState<File[]>([]);
  const [previews, setPreviews] = useState<string[]>([]);
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { t, language } = useLanguage();

  const handlePhotoUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = e.target.files;
    if (files) {
      const newFiles = Array.from(files);
      setPhotos(prev => [...prev, ...newFiles]);

      // Create previews
      newFiles.forEach(file => {
        const reader = new FileReader();
        reader.onloadend = () => {
          setPreviews(prev => [...prev, reader.result as string]);
        };
        reader.readAsDataURL(file);
      });
    }
  };

  const removePhoto = (index: number) => {
    setPhotos(prev => prev.filter((_, i) => i !== index));
    setPreviews(prev => prev.filter((_, i) => i !== index));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!description.trim()) return;

    setLoading(true);
    try {
      await onSubmit({
        description: description.trim(),
        location: location.trim(),
        date,
        photos,
      });

      setDescription("");
      setLocation("");
      setDate(new Date());
      setPhotos([]);
      setPreviews([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label className="block text-sm font-medium text-foreground mb-2">
          {t('form.whatDidYouDo')}
        </label>
        <Textarea
          value={description}
          onChange={(e) => setDescription(e.target.value.slice(0, 5000))}
          placeholder={t('form.descriptionPlaceholder')}
          className="min-h-[100px] resize-none"
          maxLength={5000}
        />
      </div>

      <div>
        <label className="block text-sm font-medium text-foreground mb-2">
          {t('form.location')}
        </label>
        <div className="relative">
          <MapPin className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <Input
            value={location}
            onChange={(e) => setLocation(e.target.value)}
            placeholder={t('form.locationPlaceholder')}
            className="pl-9"
          />
        </div>
      </div>

      <div>
        <label className="block text-sm font-medium text-foreground mb-2">
          {t('form.date')}
        </label>
        <Popover>
          <PopoverTrigger asChild>
            <Button
              variant="outline"
              className={cn(
                "w-full justify-start text-left font-normal",
                !date && "text-muted-foreground"
              )}
            >
              <CalendarIcon className="mr-2 h-4 w-4" />
              {date ? format(date, "d MMMM yyyy", { locale: dateLocales[language] }) : t('form.selectDate')}
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-auto p-0" align="start">
            <Calendar
              mode="single"
              selected={date}
              onSelect={(d) => d && setDate(d)}
              initialFocus
              className="pointer-events-auto"
            />
          </PopoverContent>
        </Popover>
      </div>

      <div>
        <label className="block text-sm font-medium text-foreground mb-2">
          {t('form.photos')}
        </label>
        <input
          type="file"
          ref={fileInputRef}
          onChange={handlePhotoUpload}
          accept="image/*"
          multiple
          className="hidden"
        />

        <div className="flex flex-wrap gap-3">
          {previews.map((preview, index) => (
            <div key={index} className="relative group animate-fade-in">
              <img
                src={preview}
                alt={`${t('form.upload')} ${index + 1}`}
                className="w-20 h-20 object-cover rounded-lg border border-border"
              />
              <button
                type="button"
                onClick={() => removePhoto(index)}
                className="absolute -top-2 -right-2 bg-destructive text-destructive-foreground rounded-full p-1 opacity-0 group-hover:opacity-100 transition-opacity"
              >
                <X className="w-3 h-3" />
              </button>
            </div>
          ))}

          <button
            type="button"
            onClick={() => fileInputRef.current?.click()}
            className="w-20 h-20 border-2 border-dashed border-border rounded-lg flex flex-col items-center justify-center text-muted-foreground hover:border-primary hover:text-primary transition-colors"
          >
            <Camera className="w-6 h-6" />
            <span className="text-xs mt-1">{t('form.photo')}</span>
          </button>
        </div>
      </div>

      <Button
        type="submit"
        className="w-full"
        disabled={!description.trim() || loading}
      >
        {loading ? (
          <Loader2 className="w-4 h-4 mr-2 animate-spin" />
        ) : (
          <Plus className="w-4 h-4 mr-2" />
        )}
        {t('form.addActivity')}
      </Button>
    </form>
  );
}

import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { useToast } from '@/hooks/use-toast';
import { Loader2, Eye, EyeOff } from 'lucide-react';
import { useLanguage } from '@/hooks/useLanguage';
import { supabase } from '@/integrations/supabase/client';

export default function ResetPasswordForm() {
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const { toast } = useToast();
  const { t } = useLanguage();
  const navigate = useNavigate();

  const handleUpdatePassword = async (e: React.FormEvent) => {
    e.preventDefault();
    if (newPassword !== confirmNewPassword) {
      toast({
        title: t('auth.error'),
        description: t('auth.passwordMismatch'),
        variant: 'destructive',
      });
      return;
    }
    setLoading(true);
    try {
      const { error } = await supabase.auth.updateUser({ password: newPassword });
      if (error) throw error;
      toast({
        title: t('auth.passwordUpdated'),
      });
      navigate('/');
    } catch (error: any) {
      toast({
        title: t('auth.error'),
        description: t('auth.passwordUpdateError'),
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleUpdatePassword} className="space-y-5">
      <div>
        <label className="block text-sm font-semibold text-foreground mb-2">
          {t('auth.newPassword')}
        </label>
        <div className="relative">
          <Input
            type={showPassword ? 'text' : 'password'}
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="••••••••"
            required
            minLength={6}
          />
          <button
            type="button"
            onClick={() => setShowPassword(!showPassword)}
            className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
          >
            {showPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
          </button>
        </div>
      </div>

      <div>
        <label className="block text-sm font-semibold text-foreground mb-2">
          {t('auth.confirmNewPassword')}
        </label>
        <div className="relative">
          <Input
            type={showConfirmPassword ? 'text' : 'password'}
            value={confirmNewPassword}
            onChange={(e) => setConfirmNewPassword(e.target.value)}
            placeholder="••••••••"
            required
            minLength={6}
          />
          <button
            type="button"
            onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
          >
            {showConfirmPassword ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
          </button>
        </div>
        {confirmNewPassword && newPassword !== confirmNewPassword && (
          <p className="text-sm text-destructive mt-1">{t('auth.passwordMismatch')}</p>
        )}
      </div>

      <Button type="submit" className="w-full" disabled={loading}>
        {loading && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
        {t('auth.updatePasswordButton')}
      </Button>
    </form>
  );
}

import { Share2, Loader2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useLanguage } from "@/hooks/useLanguage";
import { useState } from "react";
import { toast } from "sonner";

interface ShareMenuProps {
  onPrint: () => void;
  getShareText: () => string;
  generatePdf: () => Promise<Blob | null>;
  pdfFileName: string;
}

export function ShareMenu({ onPrint, getShareText, generatePdf, pdfFileName }: ShareMenuProps) {
  const { t } = useLanguage();
  const [loading, setLoading] = useState(false);

  const handleShare = async () => {
    setLoading(true);
    try {
      const blob = await generatePdf();
      if (!blob) {
        toast.error("PDF oluşturulamadı");
        return;
      }

      const file = new File([blob], pdfFileName, { type: 'application/pdf' });

      // Use native share sheet if available (mobile & modern browsers)
      if (navigator.share && navigator.canShare?.({ files: [file] })) {
        await navigator.share({
          title: t('invoice.weeklyReport'),
          text: getShareText(),
          files: [file],
        });
      } else {
        // Fallback: download the PDF directly
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = pdfFileName;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
        toast.success(t('invoice.pdfDownloaded'));
      }
    } catch (err: any) {
      // User cancelled share sheet — not an error
      if (err?.name !== 'AbortError') {
        console.error('Share failed:', err);
        toast.error("Paylaşım başarısız");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <Button variant="outline" size="sm" className="gap-2" disabled={loading} onClick={handleShare}>
      {loading ? <Loader2 className="w-4 h-4 animate-spin" /> : <Share2 className="w-4 h-4" />}
      {t('invoice.send')}
    </Button>
  );
}

import { useState, useRef, useCallback, useMemo, useEffect } from "react";
import { format } from "date-fns";
import { nl, tr, enUS, arSA } from "date-fns/locale";
import {
  CalendarIcon, Camera, Clock, MapPin, Plus, X,
  Loader2, UserPlus, LocateFixed, UserCheck, Save
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { cn } from "@/lib/utils";
import { useLanguage, Language } from "@/hooks/useLanguage";
import { toast } from "sonner";

const dateLocales: Record<Language, typeof nl> = { tr, en: enUS, nl, ar: arSA };

export interface Teammate { name: string; hours: number; }
export interface DayEntry {
  work: string; meerwerk: string; location: string; googleMapsUrl?: string;
  hours: number; photos: File[]; previews: string[]; teammates: Teammate[];
  uitvoerder: string;
}
export interface WeeklyWork {
  monday: DayEntry; tuesday: DayEntry; wednesday: DayEntry;
  thursday: DayEntry; friday: DayEntry; saturday: DayEntry;
}
export interface DayPhotos { day: string; photos: File[]; }

interface WeeklyActivityFormProps {
  onSubmit: (data: {
    description: string; location: string; date: Date;
    photos: File[]; dayPhotos: DayPhotos[];
  }) => Promise<any>;
}

const DAYS = ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'] as const;

const emptyDay = (): DayEntry => ({
  work: '', meerwerk: '', location: '', googleMapsUrl: '',
  hours: 0, photos: [], previews: [], teammates: [{ name: '', hours: 0 }],
  uitvoerder: ''
});

const STORAGE_KEY = 'weeklyWorkDraft';

export function WeeklyActivityForm({ onSubmit }: WeeklyActivityFormProps) {
  const [weeklyWork, setWeeklyWork] = useState<WeeklyWork>({
    monday: emptyDay(), tuesday: emptyDay(), wednesday: emptyDay(),
    thursday: emptyDay(), friday: emptyDay(), saturday: emptyDay(),
  });
  const [weekStart, setWeekStart] = useState<Date>(() => new Date());
  const [loading, setLoading] = useState(false);
  const [locatingDay, setLocatingDay] = useState<string | null>(null);
  const [uploadingDay, setUploadingDay] = useState<string | null>(null);
  const { t, language } = useLanguage();

  const cur = useMemo(() => ({
    date: t("activity.day"),
    weeklyWork: t("activity.workReport"),
    weeklyWorkSub: t("activity.weeklySummary"),
    hours: t("activity.hours"),
    teammates: t("form.teammates"),
    addPerson: t("form.addPerson"),
    name: t("form.personName"),
    workPlaceholder: t("days.workPlaceholder"),
    workDetails: t("activity.description"),
    extraWork: t("activity.extraWork"),
    extraWorkPlaceholder: t("form.meerwerkPlaceholder"),
    location: t("activity.location"),
    locationPlaceholder: t("form.locationPlaceholder"),
    photo: t("activity.photo"),
    gallery: t("activity.gallery"),
    add: t("common.add"),
    addActivity: t("activity.submit"),
    locationDetected: t("activity.locationDetected"),
    supervisor: t("activity.supervisor"),
    save: t("activity.save"),
    saved: t("activity.saved"),
    saving: t("activity.saving")
  }), [t]);

  useEffect(() => {
    const saved = localStorage.getItem(STORAGE_KEY);
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        if (parsed.weeklyWork) {
          const cleanedWork = { ...parsed.weeklyWork };
          DAYS.forEach(day => {
            cleanedWork[day].previews = [];
            cleanedWork[day].photos = [];
          });
          setWeeklyWork(cleanedWork);
        }
        if (parsed.weekStart) setWeekStart(new Date(parsed.weekStart));
      } catch (e) { console.error('Failed to load draft', e); }
    }
  }, []);

  useEffect(() => {
    const dataToSave = { weeklyWork, weekStart: weekStart.toISOString() };
    localStorage.setItem(STORAGE_KEY, JSON.stringify(dataToSave));
  }, [weeklyWork, weekStart]);

  const handleUitvoerderChange = (day: keyof WeeklyWork, value: string) =>
    setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], uitvoerder: value } }));

  const handleDayWorkChange = (day: keyof WeeklyWork, value: string) =>
    setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], work: value } }));

  const handleDayMeerwerkChange = (day: keyof WeeklyWork, value: string) =>
    setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], meerwerk: value } }));

  const handleDayLocationChange = (day: keyof WeeklyWork, value: string) =>
    setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], location: value } }));

  const handleDayHoursChange = (day: keyof WeeklyWork, value: string) => {
    const hours = parseFloat(value) || 0;
    setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], hours: Math.max(0, Math.min(24, hours)) } }));
  };

  const addTeammate = (day: keyof WeeklyWork) =>
    setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], teammates: [...prev[day].teammates, { name: '', hours: 0 }] } }));

  const removeTeammate = (day: keyof WeeklyWork, index: number) =>
    setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], teammates: prev[day].teammates.filter((_, i) => i !== index) } }));

  const handleDayPhotoUpload = (day: keyof WeeklyWork, e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    if (files.length === 0) return;
    setUploadingDay(day);
    try {
      const newPreviews = files.map(file => URL.createObjectURL(file));
      setWeeklyWork(prev => ({
        ...prev,
        [day]: {
          ...prev[day],
          photos: [...prev[day].photos, ...files],
          previews: [...prev[day].previews, ...newPreviews]
        }
      }));
    } catch (err) { toast.error(t("auth.error")); } finally { setUploadingDay(null); }
  };

  const handleRemovePhoto = (day: keyof WeeklyWork, index: number) => {
    setWeeklyWork(prev => {
      const dayData = prev[day];
      const newPhotos = [...dayData.photos];
      const newPreviews = [...dayData.previews];
      URL.revokeObjectURL(newPreviews[index]);
      newPhotos.splice(index, 1);
      newPreviews.splice(index, 1);
      return { ...prev, [day]: { ...dayData, photos: newPhotos, previews: newPreviews } };
    });
  };

  const fetchAddress = async (day: keyof WeeklyWork) => {
    if (!navigator.geolocation) return toast.error(t("auth.error"));
    setLocatingDay(day);
    navigator.geolocation.getCurrentPosition(async (pos) => {
      const { latitude: lat, longitude: lon } = pos.coords;
      const gMaps = `https://www.google.com/maps?q=${lat},${lon}`;
      try {
        const res = await fetch(`https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lon}&format=json`, {
          headers: { 'Accept-Language': language }
        });
        const data = await res.json();
        const finalAddr = data.display_name || "";
        setWeeklyWork(prev => ({
          ...prev, [day]: { ...prev[day], location: finalAddr || cur.locationDetected, googleMapsUrl: gMaps }
        }));
      } catch (e) {
        setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], googleMapsUrl: gMaps } }));
      } finally { setLocatingDay(null); }
    }, () => setLocatingDay(null));
  };

  const totalHours = useMemo(() =>
    DAYS.reduce((sum, day) => sum + weeklyWork[day].hours, 0),
    [weeklyWork]
  );

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      const description = JSON.stringify({ type: 'weekly', days: weeklyWork, totalHours });
      await onSubmit({
        description, location: '', date: weekStart,
        photos: DAYS.flatMap(day => weeklyWork[day].photos),
        dayPhotos: DAYS.filter(day => weeklyWork[day].photos.length > 0).map(day => ({ day, photos: weeklyWork[day].photos })),
      });
      localStorage.removeItem(STORAGE_KEY);
      toast.success(t("activity.success"));
    } finally { setLoading(false); }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-8 max-w-4xl mx-auto p-4 sm:p-6 pb-24">
      <div className="bg-card p-6 rounded-[2rem] border border-border/50 shadow-sm relative overflow-hidden group">
        <div className="absolute top-0 right-0 w-32 h-32 bg-primary/5 rounded-full -mr-16 -mt-16 transition-transform group-hover:scale-110" />
        <label className="text-[11px] font-extrabold text-muted-foreground uppercase tracking-[0.2em] mb-3 block">{cur.date}</label>
        <Popover>
          <PopoverTrigger asChild>
            <Button variant="outline" className="w-full justify-start h-14 rounded-2xl border-none bg-muted/50 font-bold hover:bg-muted transition-all text-lg group">
              <CalendarIcon className="mr-3 h-6 w-6 text-primary transition-transform group-hover:scale-110" />
              {format(weekStart, "d MMMM yyyy", { locale: dateLocales[language] })}
            </Button>
          </PopoverTrigger>
          <PopoverContent className="w-auto p-0 rounded-3xl border-none shadow-2xl" align="start">
            <Calendar mode="single" selected={weekStart} onSelect={(d) => d && setWeekStart(d)} className="rounded-3xl" />
          </PopoverContent>
        </Popover>
      </div>

      <div className="space-y-8">
        <div className="flex items-center justify-between px-2">
          <div className="space-y-1">
            <h2 className="text-3xl font-black tracking-tight text-foreground uppercase italic">{cur.weeklyWork}</h2>
            <p className="text-xs font-bold text-muted-foreground uppercase tracking-widest opacity-60">{cur.weeklyWorkSub}</p>
          </div>
          <div className="flex flex-col items-end gap-1">
            <div className="flex items-center gap-3 bg-primary text-white px-6 py-3 rounded-2xl font-black text-lg shadow-xl shadow-primary/20">
              <Clock className="w-5 h-5" /> {totalHours} <span className="opacity-80 text-sm">{cur.hours.toUpperCase()}</span>
            </div>
          </div>
        </div>

        {DAYS.map((day) => (
          <div key={day} className="group p-8 bg-card border border-border/50 rounded-[3rem] space-y-7 shadow-sm hover:shadow-xl transition-all duration-500 relative overflow-hidden">
            <div className="absolute top-0 left-0 w-1 h-full bg-primary/10 group-hover:bg-primary transition-colors" />

            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center border-b border-border/50 pb-5 gap-4">
              <span className="font-black text-2xl text-primary uppercase italic tracking-tighter block">{t(`days.${day}`)}</span>
              <div className="bg-muted/50 px-4 py-2 rounded-2xl flex items-center gap-3 w-full sm:w-auto">
                <span className="text-[10px] font-black text-muted-foreground uppercase">{cur.hours}:</span>
                <input
                  type="number" step="0.5" value={weeklyWork[day].hours || ''}
                  onChange={(e) => handleDayHoursChange(day, e.target.value)}
                  className="w-14 bg-transparent text-center font-black text-2xl text-foreground outline-none"
                  placeholder="0"
                />
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-6">
                <div className="space-y-3">
                  <div className="flex items-center gap-2 text-muted-foreground/60 ml-1">
                    <UserCheck className="w-4 h-4" />
                    <span className="text-[10px] font-black uppercase tracking-[0.2em]">{cur.supervisor}</span>
                  </div>
                  <Input
                    value={weeklyWork[day].uitvoerder} placeholder="..."
                    className="h-14 rounded-2xl bg-muted/30 border-none font-bold text-lg"
                    onChange={(e) => handleUitvoerderChange(day, e.target.value)}
                  />
                </div>

                <div className="space-y-3">
                  <div className="flex items-center gap-2 text-muted-foreground/60 ml-1">
                    <UserPlus className="w-4 h-4" />
                    <span className="text-[10px] font-black uppercase tracking-[0.2em]">{cur.teammates}</span>
                  </div>
                  <div className="space-y-3">
                    {weeklyWork[day].teammates.map((teammate, idx) => (
                      <div key={idx} className="flex gap-2">
                        <Input
                          value={teammate.name} placeholder={cur.name}
                          className="h-12 rounded-2xl bg-muted/20 border-none font-bold flex-1"
                          onChange={(e) => {
                            const newT = [...weeklyWork[day].teammates];
                            newT[idx].name = e.target.value;
                            setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], teammates: newT } }));
                          }}
                        />
                        <Input
                          type="number" placeholder="h"
                          className="w-16 h-12 rounded-2xl bg-muted/20 border-none text-center font-black"
                          onChange={(e) => {
                            const newT = [...weeklyWork[day].teammates];
                            newT[idx].hours = parseFloat(e.target.value) || 0;
                            setWeeklyWork(prev => ({ ...prev, [day]: { ...prev[day], teammates: newT } }));
                          }}
                        />
                        <Button
                          type="button" variant="ghost" size="icon"
                          onClick={() => removeTeammate(day, idx)}
                          className="text-destructive/40 hover:text-destructive rounded-2xl h-12 w-12"
                        >
                          <X className="w-5 h-5" />
                        </Button>
                      </div>
                    ))}
                    <Button
                      type="button" variant="outline" onClick={() => addTeammate(day)}
                      className="w-full h-12 rounded-2xl border-dashed border-2 bg-transparent text-xs font-black uppercase text-muted-foreground"
                    >
                      <Plus className="w-4 h-4 mr-2" /> {cur.addPerson}
                    </Button>
                  </div>
                </div>
              </div>

              <div className="space-y-4">
                <div className="space-y-1">
                  <span className="text-[10px] font-black text-muted-foreground/60 uppercase tracking-[0.2em] ml-1">{cur.workDetails}</span>
                  <Textarea
                    value={weeklyWork[day].work} onChange={(e) => handleDayWorkChange(day, e.target.value)}
                    placeholder={cur.workPlaceholder}
                    className="min-h-[120px] rounded-[2rem] bg-muted/30 border-none p-6 font-medium text-lg resize-none"
                  />
                </div>
                <div className="space-y-1">
                  <span className="text-[10px] font-black text-primary/60 uppercase tracking-[0.2em] ml-1">{cur.extraWork}</span>
                  <Textarea
                    value={weeklyWork[day].meerwerk} onChange={(e) => handleDayMeerwerkChange(day, e.target.value)}
                    placeholder={cur.extraWorkPlaceholder}
                    className="min-h-[80px] rounded-2xl bg-primary/5 border-2 border-dashed border-primary/10 p-4 text-sm font-medium resize-none"
                  />
                </div>
              </div>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 pt-4 border-t border-border/30">
              <div className="space-y-4">
                <div className="flex items-center gap-2 text-muted-foreground/60 ml-1">
                  <MapPin className="w-4 h-4" />
                  <span className="text-[10px] font-black uppercase tracking-[0.2em]">{cur.location}</span>
                </div>
                <div className="flex gap-3 items-center">
                  <Input
                    value={weeklyWork[day].location} onChange={(e) => handleDayLocationChange(day, e.target.value)}
                    placeholder={cur.locationPlaceholder}
                    className="h-14 rounded-2xl bg-muted/30 border-none font-bold"
                  />
                  <Button
                    type="button" onClick={() => fetchAddress(day)}
                    className={cn("h-14 w-14 rounded-2xl shadow-xl", weeklyWork[day].location ? "bg-green-600" : "bg-primary")}
                    disabled={locatingDay === day}
                  >
                    {locatingDay === day ? <Loader2 className="w-6 h-6 animate-spin" /> : <LocateFixed className="w-6 h-6" />}
                  </Button>
                </div>
              </div>

              <div className="space-y-4">
                <div className="flex items-center gap-2 text-muted-foreground/60 ml-1">
                  <Camera className="w-4 h-4" />
                  <span className="text-[10px] font-black uppercase tracking-[0.2em]">{cur.photo} {cur.gallery}</span>
                </div>
                <div className="flex flex-wrap gap-4">
                  {weeklyWork[day].previews.map((p, i) => (
                    <div key={i} className="relative w-24 h-24 group/img">
                      <img src={p} className="w-full h-full object-cover rounded-[1.5rem] border-4 border-white shadow-xl" />
                      <button
                        type="button" onClick={() => handleRemovePhoto(day, i)}
                        className="absolute -top-2 -right-2 bg-destructive text-white rounded-full p-2 border-2 border-white opacity-0 group-hover/img:opacity-100 transition-opacity"
                      >
                        <X className="w-3 h-3" />
                      </button>
                    </div>
                  ))}
                  <div className="relative">
                    <input type="file" id={`file-${day}`} className="hidden" multiple accept="image/*" onChange={(e) => handleDayPhotoUpload(day, e)} />
                    <Button
                      type="button" onClick={() => document.getElementById(`file-${day}`)?.click()}
                      disabled={uploadingDay === day}
                      className="w-24 h-24 rounded-[1.5rem] border-2 border-dashed border-border bg-muted/20 text-muted-foreground flex flex-col items-center justify-center gap-2"
                    >
                      {uploadingDay === day ? <Loader2 className="w-8 h-8 animate-spin" /> : <><Plus className="w-8 h-8" /><span className="text-[10px] font-black uppercase">{cur.add}</span></>}
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="sticky bottom-8 left-0 right-0 max-w-lg mx-auto px-4 z-20">
        <Button
          type="submit" disabled={loading}
          className="w-full h-20 rounded-[3rem] text-2xl font-black bg-foreground text-background hover:bg-primary hover:text-white uppercase tracking-[0.15em] shadow-2xl transition-all flex items-center justify-center gap-4"
        >
          {loading ? <Loader2 className="w-10 h-10 animate-spin" /> : <><Save className="w-8 h-8" /> {cur.addActivity}</>}
        </Button>
      </div>
    </form>
  );
}

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";

import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center rounded-lg border px-3 py-1 text-xs font-semibold transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default: "border-transparent bg-gradient-to-r from-primary to-red-700 text-primary-foreground shadow-sm",
        secondary: "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive: "border-transparent bg-gradient-to-r from-destructive to-red-800 text-destructive-foreground shadow-sm",
        outline: "text-foreground border-2",
        success: "border-transparent bg-gradient-to-r from-green-500 to-emerald-600 text-white shadow-sm",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  },
);

export interface BadgeProps extends React.HTMLAttributes<HTMLDivElement>, VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
