import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from './useAuth';
import { useToast } from './use-toast';

export interface ActivityPhoto {
  id: string;
  file_url: string;
  day: string | null;
}

export interface Activity {
  id: string;
  user_id: string;
  description: string;
  location: string | null;
  activity_date: string;
  created_at: string;
  photos: ActivityPhoto[];
  profile?: {
    full_name: string | null;
    email: string;
  };
}

function sanitizeFileName(name: string): string {
  const ext = name.split('.').pop()?.toLowerCase().replace(/[^a-z0-9]/g, '') || 'jpg';
  const base = name.replace(/\.[^.]+$/, '').replace(/[^a-zA-Z0-9._-]/g, '_').replace(/^\.+/, '').substring(0, 200);
  return `${base}.${ext}`;
}

export function useActivities(filterUserId?: string | null) {
  const [activities, setActivities] = useState<Activity[]>([]);
  const [loading, setLoading] = useState(true);
  const { user, isAdmin } = useAuth();
  const { toast } = useToast();

  const fetchActivities = async () => {
    if (!user) return;

    setLoading(true);
    try {
      // Build query - RLS will handle filtering based on role
      let query = supabase
        .from('activities')
        .select('*')
        .order('created_at', { ascending: false });

      // Admin can filter by specific user
      if (isAdmin && filterUserId) {
        query = query.eq('user_id', filterUserId);
      }

      const { data: activitiesData, error: activitiesError } = await query;

      if (activitiesError) throw activitiesError;

      // Fetch ALL photos in one query instead of per-activity
      const activityIds = (activitiesData || []).map(a => a.id);
      const { data: allPhotosData } = activityIds.length > 0
        ? await supabase
          .from('activity_photos')
          .select('id, file_url, day, activity_id')
          .in('activity_id', activityIds)
        : { data: [] };

      // Batch generate signed URLs for all photos at once
      const pathPhotos = (allPhotosData || []).filter(p => !p.file_url.startsWith('http'));
      const paths = pathPhotos.map(p => p.file_url);

      const signedUrlMap: Record<string, string> = {};
      if (paths.length > 0) {
        // createSignedUrls supports batch - one request for all photos
        const { data: signedUrls } = await supabase.storage
          .from('activity-photos')
          .createSignedUrls(paths, 3600);
        if (signedUrls) {
          signedUrls.forEach((item) => {
            if (item.signedUrl && item.path) {
              signedUrlMap[item.path] = item.signedUrl;
            }
          });
        }
      }

      // Group photos by activity_id
      const photosByActivity: Record<string, ActivityPhoto[]> = {};
      for (const p of (allPhotosData || [])) {
        const isPath = !p.file_url.startsWith('http');
        const url = isPath ? (signedUrlMap[p.file_url] || p.file_url) : p.file_url;
        if (!photosByActivity[p.activity_id]) photosByActivity[p.activity_id] = [];
        photosByActivity[p.activity_id].push({ id: p.id, file_url: url, day: p.day });
      }

      // If admin, fetch ALL profiles in one query
      const profileMap: Record<string, { full_name: string | null; email: string } | undefined> = {};
      if (isAdmin) {
        const userIds = [...new Set((activitiesData || []).map(a => a.user_id))];
        if (userIds.length > 0) {
          const { data: profilesData } = await supabase
            .from('profiles')
            .select('id, full_name, email')
            .in('id', userIds);
          for (const p of (profilesData || [])) {
            profileMap[p.id] = { full_name: p.full_name, email: p.email };
          }
        }
      }

      const activitiesWithPhotos = (activitiesData || []).map((activity) => ({
        ...activity,
        photos: photosByActivity[activity.id] || [],
        profile: isAdmin ? profileMap[activity.user_id] : undefined,
      }));

      setActivities(activitiesWithPhotos);
    } catch (error: any) {
      toast({
        title: 'Hata',
        description: error.message,
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const addActivity = async (data: {
    description: string;
    location: string;
    date: Date;
    photos: File[];
    dayPhotos?: { day: string; photos: File[] }[];
  }) => {
    if (!user) return;

    try {
      // Validate inputs
      const description = data.description?.trim() || '';
      const location = data.location?.trim() || null;
      if (!description || description.length > 5000) {
        throw new Error('Description must be between 1 and 5000 characters');
      }
      if (location && location.length > 200) {
        throw new Error('Location must be under 200 characters');
      }

      // Insert activity
      const { data: activity, error: activityError } = await supabase
        .from('activities')
        .insert({
          user_id: user.id,
          description,
          location,
          activity_date: data.date.toISOString().split('T')[0],
        })
        .select()
        .single();

      if (activityError) throw activityError;

      // Upload photos with day info
      if (data.dayPhotos && data.dayPhotos.length > 0) {
        // Weekly form with per-day photos
        for (const dayEntry of data.dayPhotos) {
          for (const photo of dayEntry.photos) {
            const fileName = `${user.id}/${activity.id}/${Date.now()}-${sanitizeFileName(photo.name)}`;

            const { error: uploadError } = await supabase.storage
              .from('activity-photos')
              .upload(fileName, photo);

            if (uploadError) throw uploadError;

            // Save file path reference (not signed URL)
            await supabase.from('activity_photos').insert({
              activity_id: activity.id,
              file_url: fileName,
              day: dayEntry.day,
            });
          }
        }
      } else {
        // Simple form without day info
        for (const photo of data.photos) {
          const fileName = `${user.id}/${activity.id}/${Date.now()}-${sanitizeFileName(photo.name)}`;

          const { error: uploadError } = await supabase.storage
            .from('activity-photos')
            .upload(fileName, photo);

          if (uploadError) throw uploadError;

          // Save file path reference (not signed URL)
          await supabase.from('activity_photos').insert({
            activity_id: activity.id,
            file_url: fileName,
          });
        }
      }

      await fetchActivities();

      toast({
        title: 'Aktivite eklendi',
        description: 'Aktiviteniz başarıyla kaydedildi.',
      });

      return activity;
    } catch (error: any) {
      console.error("Activity submission error:", error);
      toast({
        title: 'Hata',
        description: error.message + (error.details ? ` (${error.details})` : "") + (error.hint ? ` - Hint: ${error.hint}` : ""),
        variant: 'destructive',
      });
      return null;
    }
  };

  const updateActivity = async (id: string, data: {
    description: string;
    location?: string;
    date?: Date;
  }) => {
    try {
      const description = data.description?.trim() || '';
      const location = data.location?.trim() || null;
      if (!description || description.length > 5000) {
        throw new Error('Description must be between 1 and 5000 characters');
      }
      if (location && location.length > 200) {
        throw new Error('Location must be under 200 characters');
      }

      const updatePayload: any = {
        description,
        location,
      };

      if (data.date) {
        updatePayload.activity_date = data.date.toISOString().split('T')[0];
      }

      const { error } = await supabase
        .from('activities')
        .update(updatePayload)
        .eq('id', id);

      if (error) throw error;

      await fetchActivities();

      toast({
        title: 'Aktivite güncellendi',
        description: 'Aktivite başarıyla güncellendi.',
      });

      return true;
    } catch (error: any) {
      toast({
        title: 'Hata',
        description: error.message,
        variant: 'destructive',
      });
      return false;
    }
  };

  const deleteActivity = async (id: string) => {
    try {
      const { error } = await supabase
        .from('activities')
        .delete()
        .eq('id', id);

      if (error) throw error;

      setActivities(prev => prev.filter(a => a.id !== id));

      toast({
        title: 'Aktivite silindi',
        description: 'Aktivite başarıyla silindi.',
      });
    } catch (error: any) {
      toast({
        title: 'Hata',
        description: error.message,
        variant: 'destructive',
      });
    }
  };

  const deletePhoto = async (photoId: string) => {
    try {
      const { error } = await supabase
        .from('activity_photos')
        .delete()
        .eq('id', photoId);

      if (error) throw error;
      return true;
    } catch (error: any) {
      toast({
        title: 'Hata',
        description: error.message,
        variant: 'destructive',
      });
      return false;
    }
  };

  const uploadPhotosForActivity = async (activityId: string, photos: File[], day?: string) => {
    if (!user) return false;
    try {
      for (const photo of photos) {
        const fileName = `${user.id}/${activityId}/${Date.now()}-${sanitizeFileName(photo.name)}`;

        const { error: uploadError } = await supabase.storage
          .from('activity-photos')
          .upload(fileName, photo);

        if (uploadError) throw uploadError;

        // Save file path reference (not signed URL)
        await supabase.from('activity_photos').insert({
          activity_id: activityId,
          file_url: fileName,
          day: day || null,
        });
      }
      return true;
    } catch (error: any) {
      toast({
        title: 'Hata',
        description: error.message,
        variant: 'destructive',
      });
      return false;
    }
  };

  useEffect(() => {
    if (user) {
      fetchActivities();
    }
  }, [user, isAdmin, filterUserId]);

  return {
    activities,
    loading,
    addActivity,
    updateActivity,
    deleteActivity,
    deletePhoto,
    uploadPhotosForActivity,
    refetch: fetchActivities,
  };
}

import { useState, useEffect, createContext, useContext, ReactNode } from "react";
import { User, Session } from "@supabase/supabase-js";
import { supabase } from "@/integrations/supabase/client";

interface AuthContextType {
  user: User | null;
  session: Session | null;
  isAdmin: boolean;
  loading: boolean;
  signIn: (email: string, password: string) => Promise<{ error: Error | null }>;
  signUp: (
    email: string,
    password: string,
    fullName: string,
    language?: string
  ) => Promise<{ error: Error | null; data?: any }>;
  signOut: () => Promise<void>;
  resetPassword: (email: string, language?: string) => Promise<{ error: Error | null }>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);

  const checkAdminRole = async (userId: string) => {
    const { data } = await supabase
      .from("user_roles")
      .select("role")
      .eq("user_id", userId)
      .eq("role", "admin")
      .maybeSingle();

    setIsAdmin(!!data);
  };

  const checkBlockedStatus = async (userId: string) => {
    const { data } = await supabase
      .from("profiles")
      .select("is_blocked")
      .eq("id", userId)
      .maybeSingle();

    if (data?.is_blocked) {
      await supabase.auth.signOut();
      setUser(null);
      setSession(null);
      setIsAdmin(false);
    }
  };

  useEffect(() => {
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange(async (_event, session) => {
      setSession(session);
      setUser(session?.user ?? null);

      if (session?.user) {
        checkAdminRole(session.user.id);
        checkBlockedStatus(session.user.id);
      } else {
        setIsAdmin(false);
      }

      setLoading(false);
    });

    supabase.auth.getSession().then(({ data: { session } }) => {
      setSession(session);
      setUser(session?.user ?? null);

      if (session?.user) {
        checkAdminRole(session.user.id);
        checkBlockedStatus(session.user.id);
      }

      setLoading(false);
    });

    return () => subscription.unsubscribe();
  }, []);

  const signIn = async (email: string, password: string) => {
    const { error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    return { error };
  };

  // Profesyonel/Test bypass signup
  const signUp = async (
    email: string,
    password: string,
    fullName: string,
    language: string = "tr"
  ) => {
    const { error, data } = await supabase.functions.invoke("send-auth-email", {
      body: {
        type: "signup_bypass", // Using bypass type to auto-confirm for testing
        email,
        password,
        fullName,
        language,
        redirectTo: window.location.origin + "/home",
      },
    });

    return { error, data };
  };

  const signOut = async () => {
    await supabase.auth.signOut();
    setUser(null);
    setSession(null);
    setIsAdmin(false);
  };

  const resetPassword = async (email: string, language: string = "tr") => {
    const { error } = await supabase.auth.resetPasswordForEmail(email, {
      redirectTo: `${window.location.origin}/auth`,
    });
    return { error: error as Error | null };
  };

  return (
    <AuthContext.Provider
      value={{ user, session, isAdmin, loading, signIn, signUp, signOut, resetPassword }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
}

import { useState, useEffect, useRef } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useLanguage } from './useLanguage';
import { useAuth } from './useAuth';

// Global translation cache
const translationCache: Record<string, Record<string, string>> = {};

// Global request queue to prevent rate limiting
let requestQueue: Array<() => Promise<void>> = [];
let isProcessingQueue = false;
let lastRequestTime = 0;
const MIN_REQUEST_INTERVAL = 2500;

// Circuit breaker: stop trying after consecutive failures
let consecutiveFailures = 0;
const MAX_FAILURES = 3;
let circuitOpenUntil = 0;

async function processQueue() {
  if (isProcessingQueue || requestQueue.length === 0) return;

  // Circuit breaker: if too many failures, stop for 60 seconds
  if (consecutiveFailures >= MAX_FAILURES && Date.now() < circuitOpenUntil) {
    requestQueue = [];
    return;
  }

  isProcessingQueue = true;

  while (requestQueue.length > 0) {
    if (consecutiveFailures >= MAX_FAILURES && Date.now() < circuitOpenUntil) {
      requestQueue = [];
      break;
    }

    const timeSinceLastRequest = Date.now() - lastRequestTime;
    if (timeSinceLastRequest < MIN_REQUEST_INTERVAL) {
      await new Promise(resolve => setTimeout(resolve, MIN_REQUEST_INTERVAL - timeSinceLastRequest));
    }

    const nextRequest = requestQueue.shift();
    if (nextRequest) {
      lastRequestTime = Date.now();
      await nextRequest();
    }
  }

  isProcessingQueue = false;
}

export function useAutoTranslate(text: string, enabled: boolean = true) {
  const { language } = useLanguage();
  const { session } = useAuth();
  const [translatedText, setTranslatedText] = useState(text);
  const [translating, setTranslating] = useState(false);
  const mountedRef = useRef(true);
  const requestIdRef = useRef(0);

  useEffect(() => {
    mountedRef.current = true;
    return () => { mountedRef.current = false; };
  }, []);

  useEffect(() => {
    if (!enabled || !text?.trim() || !session) {
      setTranslatedText(text);
      return;
    }

    const cacheKey = text.slice(0, 100);

    // Check cache first
    if (translationCache[cacheKey]?.[language]) {
      setTranslatedText(translationCache[cacheKey][language]);
      return;
    }

    // Generate unique request ID
    const currentRequestId = ++requestIdRef.current;
    setTranslating(true);

    const translateText = async () => {
      try {
        const { data, error } = await supabase.functions.invoke('translate', {
          body: { text, targetLanguage: language }
        });

        if (!mountedRef.current || currentRequestId !== requestIdRef.current) return;

        if (!error && data?.translatedText) {
          consecutiveFailures = 0; // Reset on success
          if (!translationCache[cacheKey]) {
            translationCache[cacheKey] = {};
          }
          translationCache[cacheKey][language] = data.translatedText;
          setTranslatedText(data.translatedText);
        } else {
          consecutiveFailures++;
          if (consecutiveFailures >= MAX_FAILURES) {
            circuitOpenUntil = Date.now() + 60000; // Pause for 60s
          }
          setTranslatedText(text);
        }
      } catch {
        consecutiveFailures++;
        if (consecutiveFailures >= MAX_FAILURES) {
          circuitOpenUntil = Date.now() + 60000;
        }
        if (mountedRef.current && currentRequestId === requestIdRef.current) {
          setTranslatedText(text);
        }
      } finally {
        if (mountedRef.current && currentRequestId === requestIdRef.current) {
          setTranslating(false);
        }
      }
    };

    // Add to queue instead of calling directly
    requestQueue.push(translateText);
    processQueue();

    return () => {
      // Cancel this specific request by incrementing requestId
      requestIdRef.current++;
    };
  }, [text, language, enabled, session]);

  return { translatedText, translating };
}

import { useState, useEffect } from "react";
import { Navigate, useNavigate } from "react-router-dom";
import {
  ArrowLeft,
  UserPlus,
  Trash2,
  Ban,
  CheckCircle,
  Loader2,
  Shield,
} from "lucide-react";

import { useAuth } from "@/hooks/useAuth";
import { useLanguage } from "@/hooks/useLanguage";
import { supabase } from "@/integrations/supabase/client";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { LanguageSelector } from "@/components/LanguageSelector";

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";

import {
  Dialog,
  DialogContent,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogTrigger,
} from "@/components/ui/dialog";

import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
  AlertDialogDescription,
} from "@/components/ui/alert-dialog";

import { toast } from "sonner";
import { Logo } from "@/components/Logo";

interface UserProfile {
  id: string;
  email: string;
  full_name: string | null;
  is_blocked: boolean;
  created_at: string;
  role: "admin" | "staff";
}

const AdminPanel = () => {
  const { user, isAdmin, loading: authLoading } = useAuth();
  const { t } = useLanguage();
  const navigate = useNavigate();

  const [users, setUsers] = useState<UserProfile[]>([]);
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState<string | null>(null);

  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [newUserEmail, setNewUserEmail] = useState("");
  const [newUserPassword, setNewUserPassword] = useState("");
  const [newUserFullName, setNewUserFullName] = useState("");
  const [addingUser, setAddingUser] = useState(false);

  /* ---------------- FETCH USERS ---------------- */
  useEffect(() => {
    if (isAdmin) fetchUsers();
  }, [isAdmin]);

  const fetchUsers = async () => {
    try {
      setLoading(true);

      const { data: profiles, error: profilesError } = await supabase
        .from("profiles")
        .select("id,email,full_name,is_blocked,created_at")
        .order("created_at", { ascending: false });

      if (profilesError) throw profilesError;

      const { data: roles, error: rolesError } = await supabase
        .from("user_roles")
        .select("user_id,role");

      if (rolesError) throw rolesError;

      const usersWithRoles: UserProfile[] = (profiles || []).map(
        (profile: any) => {
          const userRole = roles?.find((r) => r.user_id === profile.id);

          return {
            ...profile,
            role: userRole?.role || "staff",
          };
        }
      );

      setUsers(usersWithRoles);
    } catch (error) {
      console.error(error);
      toast.error(t("adminPanel.fetchError"));
    } finally {
      setLoading(false);
    }
  };

  /* ---------------- ADD USER ---------------- */
  const handleAddUser = async () => {
    if (!newUserEmail || !newUserPassword || !newUserFullName) {
      toast.error(t("adminPanel.fillAllFields"));
      return;
    }

    setAddingUser(true);

    try {
      const { data: { session } } = await supabase.auth.getSession();

      const response = await supabase.functions.invoke("admin-users", {
        body: {
          action: "create",
          email: newUserEmail,
          password: newUserPassword,
          fullName: newUserFullName,
        },
        headers: {
          Authorization: `Bearer ${session?.access_token}`,
        },
      });

      if (response.error) {
        const errorMsg = await (async () => {
          if (typeof response.error === 'object' && 'context' in response.error) {
            try {
              const body = await (response.error as any).context.json();
              if (body && body.error) return body.error;
            } catch (e) {
              console.error("Could not parse error body", e);
            }
          }
          return response.error.message;
        })();

        throw new Error(errorMsg || t("adminPanel.addError"));
      }

      toast.success(t("adminPanel.userAdded"));

      setNewUserEmail("");
      setNewUserPassword("");
      setNewUserFullName("");
      setIsAddDialogOpen(false);

      fetchUsers();
    } catch (error: any) {
      toast.error(error.message || t("adminPanel.addError"));
    } finally {
      setAddingUser(false);
    }
  };

  /* ---------------- DELETE USER ---------------- */
  const handleDeleteUser = async (userId: string) => {
    setActionLoading(userId);

    try {
      const { data: { session } } = await supabase.auth.getSession();

      console.log("Calling Edge Function with token:", !!session?.access_token);

      const response = await supabase.functions.invoke("admin-users", {
        body: { action: "delete", userId },
        headers: {
          Authorization: `Bearer ${session?.access_token}`,
        },
      });

      console.log("Edge Function Response Data:", response.data);
      console.log("Edge Function Response Error:", response.error);

      if (response.error) {
        const errorMsg = await (async () => {
          if (typeof response.error === 'object' && 'context' in response.error) {
            try {
              const body = await (response.error as any).context.json();
              if (body && body.error) return body.error;
            } catch (e) {
              console.error("Could not parse error body", e);
            }
          }
          return response.error.message;
        })();

        throw new Error(errorMsg || t("adminPanel.deleteError"));
      }

      toast.success(t("adminPanel.userDeleted"));
      fetchUsers();
    } catch (error: any) {
      console.error("Delete error details:", error);
      toast.error(error.message || t("adminPanel.deleteError"));
    } finally {
      setActionLoading(null);
    }
  };

  /* ---------------- BLOCK / UNBLOCK ---------------- */
  const handleToggleBlock = async (
    userId: string,
    currentlyBlocked: boolean
  ) => {
    setActionLoading(userId);

    try {
      const { error } = await supabase
        .from("profiles")
        .update({ is_blocked: !currentlyBlocked })
        .eq("id", userId);

      if (error) throw error;

      toast.success(
        currentlyBlocked ? t("adminPanel.userUnblocked") : t("adminPanel.userBlocked")
      );

      fetchUsers();
    } catch {
      toast.error(t("adminPanel.blockError"));
    } finally {
      setActionLoading(null);
    }
  };

  /* ---------------- ROLE CHANGE (ADMIN VER / AL) ---------------- */
  const handleToggleRole = async (
    userId: string,
    currentRole: "admin" | "staff"
  ) => {
    setActionLoading(userId);

    try {
      const newRole = currentRole === "admin" ? "staff" : "admin";

      await supabase.from("user_roles").delete().eq("user_id", userId);

      const { error } = await supabase.from("user_roles").insert({
        user_id: userId,
        role: newRole,
      });

      if (error) throw error;

      toast.success(
        newRole === "admin"
          ? t("adminPanel.adminAssigned")
          : t("adminPanel.adminRemoved")
      );

      fetchUsers();
    } catch (error) {
      toast.error(t("adminPanel.blockError"));
    } finally {
      setActionLoading(null);
    }
  };

  /* ---------------- AUTH GUARDS ---------------- */
  if (authLoading)
    return (
      <div className="min-h-screen flex items-center justify-center">
        <Loader2 className="w-8 h-8 animate-spin" />
      </div>
    );

  if (!user) return <Navigate to="/auth" replace />;
  if (!isAdmin) return <Navigate to="/" replace />;

  /* ---------------- UI ---------------- */
  return (
    <div className="min-h-screen bg-background">
      {/* HEADER */}
      <header className="sticky top-0 bg-card border-b">
        <div className="container max-w-4xl mx-auto px-4 py-3 flex justify-between">
          <div className="flex items-center gap-2">
            <Button variant="ghost" size="icon" onClick={() => navigate("/")}>
              <ArrowLeft className="w-5 h-5" />
            </Button>

            <Logo className="h-7 hidden sm:block" />

            <h1 className="font-semibold flex items-center gap-2">
              <Shield className="w-4 h-4" />
              {t('adminPanel.title')}
            </h1>
          </div>

          <div className="flex items-center gap-2">
            <LanguageSelector />

            <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
              <DialogTrigger asChild>
                <Button size="icon">
                  <UserPlus className="w-4 h-4" />
                </Button>
              </DialogTrigger>

              <DialogContent>
                <DialogHeader>
                  <DialogTitle>{t('adminPanel.addUserTitle')}</DialogTitle>
                  <DialogDescription>
                    {t('adminPanel.addUserDesc')}
                  </DialogDescription>
                </DialogHeader>

                <div className="space-y-3">
                  <div>
                    <Label>{t('auth.fullName')}</Label>
                    <Input
                      value={newUserFullName}
                      onChange={(e) => setNewUserFullName(e.target.value)}
                    />
                  </div>

                  <div>
                    <Label>{t('auth.email')}</Label>
                    <Input
                      value={newUserEmail}
                      onChange={(e) => setNewUserEmail(e.target.value)}
                    />
                  </div>

                  <div>
                    <Label>{t('auth.password')}</Label>
                    <Input
                      type="password"
                      value={newUserPassword}
                      onChange={(e) => setNewUserPassword(e.target.value)}
                    />
                  </div>
                </div>

                <DialogFooter>
                  <Button
                    onClick={handleAddUser}
                    disabled={addingUser}
                  >
                    {addingUser && (
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    )}
                    {t('adminPanel.addUser')}
                  </Button>
                </DialogFooter>
              </DialogContent>
            </Dialog>
          </div>
        </div>
      </header>

      {/* TABLE */}
      <main className="container max-w-4xl mx-auto px-4 py-6">
        {loading ? (
          <div className="flex justify-center py-10">
            <Loader2 className="w-8 h-8 animate-spin" />
          </div>
        ) : (
          <div className="border rounded-lg">
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>{t('adminPanel.name')}</TableHead>
                  <TableHead>{t('auth.email')}</TableHead>
                  <TableHead>{t('adminPanel.role')}</TableHead>
                  <TableHead>{t('adminPanel.status')}</TableHead>
                  <TableHead className="text-right">{t('adminPanel.actions')}</TableHead>
                </TableRow>
              </TableHeader>

              <TableBody>
                {users.map((u) => (
                  <TableRow key={u.id}>
                    <TableCell>{u.full_name || "-"}</TableCell>
                    <TableCell>{u.email}</TableCell>

                    <TableCell>
                      <Badge>
                        {u.role === "admin" ? t('index.admin') : t('adminPanel.staff')}
                      </Badge>
                    </TableCell>

                    <TableCell>
                      {u.is_blocked ? (
                        <Badge variant="destructive">{t('adminPanel.blocked')}</Badge>
                      ) : (
                        <Badge variant="outline">{t('adminPanel.active')}</Badge>
                      )}
                    </TableCell>

                    <TableCell className="text-right">
                      {u.id !== user.id && (
                        <div className="flex gap-2 justify-end">

                          {/* ROLE BUTTON */}
                          <Button
                            size="sm"
                            variant="secondary"
                            onClick={() =>
                              handleToggleRole(u.id, u.role)
                            }
                            disabled={actionLoading === u.id}
                          >
                            <Shield className="w-4 h-4" />
                          </Button>

                          {/* BLOCK BUTTON */}
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() =>
                              handleToggleBlock(u.id, u.is_blocked)
                            }
                            disabled={actionLoading === u.id}
                          >
                            {u.is_blocked ? (
                              <CheckCircle className="w-4 h-4" />
                            ) : (
                              <Ban className="w-4 h-4" />
                            )}
                          </Button>

                          {/* DELETE */}
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button size="sm" variant="destructive">
                                <Trash2 className="w-4 h-4" />
                              </Button>
                            </AlertDialogTrigger>

                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>
                                  {t('adminPanel.deleteConfirmTitle')}
                                </AlertDialogTitle>
                                <AlertDialogDescription>
                                  {t('adminPanel.deleteConfirmDesc').replace('{name}', u.full_name || u.email)}
                                </AlertDialogDescription>
                              </AlertDialogHeader>

                              <AlertDialogFooter>
                                <AlertDialogCancel>{t('edit.cancel')}</AlertDialogCancel>
                                <AlertDialogAction
                                  onClick={() => handleDeleteUser(u.id)}
                                >
                                  {t('adminPanel.delete')}
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>
                        </div>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </div>
        )}
      </main>
    </div>
  );
};

export default AdminPanel;

